package core.training;

public class Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String s=new String("Susan");

String n="Susan";

System.out.println(s==n);
System.out.println(s.equals(n));
String s1="Dolly"; //creating string literal
char a[]= {'a','e','i','o','u'}; //creating a char array
String s2=new String(a);// converting char array to string
String s3=new String("Pussy");
System.out.println(s1);
System.out.println(s2);
System.out.println(s3);


	}

}
